import React, { useState, useEffect } from "react";
import axios from "axios";

function ReviewPage() {
  const [sets, setSets] = useState([]);
  const [selectedSet, setSelectedSet] = useState(null);
  const [flashcards, setFlashcards] = useState([]);
  const [filter, setFilter] = useState("All");

  // fetch available sets
  useEffect(() => {
    axios.get("http://localhost:5000/api/flashcards/sets").then((res) => {
      setSets(res.data);
    });
  }, []);

  // fetch flashcards for selected set
  useEffect(() => {
    if (selectedSet) {
      axios.get(`http://localhost:5000/api/flashcards/set/${selectedSet}`).then((res) => {
        setFlashcards(res.data);
      });
    }
  }, [selectedSet]);

  const filteredFlashcards = flashcards.filter((card) => {
    if (filter === "All") return true;
    return card.marks === parseInt(filter);
  });

  return (
    <div className="review-page">
      <h2>Review Flashcards</h2>

      {/* Left side set selector */}
      <div className="sets-sidebar">
        <h3>Available Sets</h3>
        <ul>
          {sets.map((s, i) => (
            <li key={i} onClick={() => setSelectedSet(s)}>
              {s}
            </li>
          ))}
        </ul>
      </div>

      {/* Right side flashcards */}
      <div className="flashcards-section">
        {selectedSet ? (
          <>
            <h3>Set: {selectedSet}</h3>
            <div className="filter-buttons">
              <button onClick={() => setFilter("All")}>All</button>
              <button onClick={() => setFilter("1")}>1 Mark</button>
              <button onClick={() => setFilter("2")}>2 Mark</button>
              <button onClick={() => setFilter("5")}>5 Mark</button>
              <button onClick={() => setFilter("10")}>10 Mark</button>
            </div>

            {filteredFlashcards.map((card, idx) => (
              <div key={idx} className="flashcard">
                <h4>{card.marks}-Mark Q{idx + 1}: {card.question}</h4>
                <pre>{card.answer}</pre>
              </div>
            ))}
          </>
        ) : (
          <p>Select a set from the left to start reviewing.</p>
        )}
      </div>
    </div>
  );
}

export default ReviewPage;
