import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ReviewPage.css";

const ReviewFlashcards = () => {
  const [flashcards, setFlashcards] = useState([]);
  const [filteredFlashcards, setFilteredFlashcards] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [history, setHistory] = useState([]);

  useEffect(() => {
    // Backend la irundhu flashcards fetch pannunga
    // Example dummy data
    const data = [
      { question: "What is AI?", answer: "Artificial Intelligence" },
      { question: "Capital of India?", answer: "New Delhi" },
      { question: "React is a?", answer: "JavaScript library" },
    ];
    setFlashcards(data);
    setFilteredFlashcards(data);
  }, []);

  // üîç Search button/symbol click pannum pothu
  const handleSearch = () => {
    if (!searchTerm) return;

    const results = flashcards.filter((flashcard) => {
      const q = flashcard.question || "";
      const a = flashcard.answer || "";

      return (
        q.toLowerCase().includes(searchTerm.toLowerCase()) ||
        a.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });

    setFilteredFlashcards(results);

    // Add to history
    setHistory((prev) => [searchTerm, ...prev]);
  };

  // üïò History click pannum pothu
  const handleHistoryClick = (term) => {
    if (!term) return;

    const results = flashcards.filter((flashcard) => {
      const q = flashcard.question || "";
      const a = flashcard.answer || "";

      return (
        q.toLowerCase().includes(term.toLowerCase()) ||
        a.toLowerCase().includes(term.toLowerCase())
      );
    });

    setFilteredFlashcards(results);
    setSearchTerm(term);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>üìö Review Flashcards</h2>

      {/* Search box */}
      <div>
        <input
          type="text"
          placeholder="Search flashcards..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>üîç</button>
      </div>

      {/* Search history */}
      <div style={{ marginTop: "10px" }}>
        <h3>üïò Your Search History</h3>
        {history.map((item, index) => (
          <button
            key={index}
            style={{ margin: "5px" }}
            onClick={() => handleHistoryClick(item)}
          >
            {item}
          </button>
        ))}
      </div>

      {/* Flashcard results */}
      <div style={{ marginTop: "20px" }}>
        {filteredFlashcards.length > 0 ? (
          filteredFlashcards.map((card, index) => (
            <div
              key={index}
              style={{
                border: "1px solid gray",
                padding: "10px",
                margin: "5px 0",
              }}
            >
              <p>
                <strong>Q:</strong> {card.question}
              </p>
              <p>
                <strong>A:</strong> {card.answer}
              </p>
            </div>
          ))
        ) : (
          <p>No flashcards found.</p>
        )}
      </div>
    </div>
  );
};

export default ReviewFlashcards;
