import React, { useEffect, useState } from 'react';
import './ReviewPage.css';

const ReviewPage = () => {
  const [history, setHistory] = useState([]);
  const [selectedUpload, setSelectedUpload] = useState(null);
  const [filteredCards, setFilteredCards] = useState([]);
  const [selectedMark, setSelectedMark] = useState(null);
  const [menuOpenIndex, setMenuOpenIndex] = useState(null); // for 3-dot menu

  // Load history from localStorage
  useEffect(() => {
    const savedHistory = JSON.parse(localStorage.getItem('flashcardHistory')) || [];
    setHistory(savedHistory);

    if (savedHistory.length > 0) {
      const latest = savedHistory[savedHistory.length - 1];
      setSelectedUpload(latest);
      setFilteredCards(latest.cards); // show all by default
    }
  }, []);

  const handleFilter = (marks) => {
    setSelectedMark(marks);
    if (!selectedUpload) return;
    const filtered = selectedUpload.cards.filter(
      (c) => Number(c.marks || 1) === Number(marks)
    );
    setFilteredCards(filtered);
  };

  const handleSelectUpload = (upload) => {
    setSelectedUpload(upload);
    setSelectedMark(null);
    setFilteredCards(upload.cards || []);
  };

  const countByMarks = (marks) =>
    selectedUpload?.cards.filter((c) => Number(c.marks || 1) === Number(marks)).length || 0;

  const handleDeleteUpload = (idx) => {
    const newHistory = [...history];
    newHistory.splice(idx, 1);
    setHistory(newHistory);
    localStorage.setItem('flashcardHistory', JSON.stringify(newHistory));

    if (selectedUpload === history[idx]) {
      if (newHistory.length > 0) {
        setSelectedUpload(newHistory[newHistory.length - 1]);
        setFilteredCards(newHistory[newHistory.length - 1].cards || []);
      } else {
        setSelectedUpload(null);
        setFilteredCards([]);
      }
    }
  };

  return (
    <div
      className="review-page-container"
      style={{
        display: 'flex',
        minHeight: '100vh',
        backgroundColor: '#e0f0ff', // page background
      }}
    >
      {/* Left Sidebar: History */}
      <div
        className="history-sidebar"
        style={{
          width: '220px',
          borderRight: '2px solid #ddd',
          padding: '1rem',
          display: 'flex',
          flexDirection: 'column',
          gap: '10px',
        }}
      >
        <h3>📜 Upload History</h3>
        {history.length === 0 && <p>No uploads yet.</p>}
        {history.map((upload, idx) => (
          <div key={idx} style={{ position: 'relative' }}>
            <button
              style={{
                width: '100%',
                padding: '8px',
                border: selectedUpload === upload ? '2px solid #007bff' : '1px solid #ccc',
                borderRadius: '5px',
                backgroundColor: selectedUpload === upload ? '#d0eaff' : '#fff',
                cursor: 'pointer',
                textAlign: 'left',
              }}
              onClick={() => handleSelectUpload(upload)}
            >
              {upload.filename || `Upload ${idx + 1}`} <br />
              <small>{upload.date}</small>
            </button>

            {/* 3-dot menu */}
            <div
              style={{
                position: 'absolute',
                top: '5px',
                right: '5px',
                cursor: 'pointer',
                fontWeight: 'bold',
              }}
              onClick={() =>
                setMenuOpenIndex(menuOpenIndex === idx ? null : idx)
              }
            >
              ⋮
            </div>

            {/* Delete option */}
            {menuOpenIndex === idx && (
              <div
                style={{
                  position: 'absolute',
                  top: '25px',
                  right: '5px',
                  backgroundColor: '#fff',
                  border: '1px solid #ccc',
                  borderRadius: '5px',
                  zIndex: 10,
                }}
              >
                <button
                  style={{
                    padding: '5px 10px',
                    backgroundColor: '#ffcccc',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                  }}
                  onClick={() => handleDeleteUpload(idx)}
                >
                  Delete
                </button>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Right/Main Review Area */}
      <div
        className="review-main"
        style={{
          flex: 1,
          padding: '1rem',
          display: 'flex',
          flexDirection: 'column',
        }}
      >
        <h2>📚 Review Flashcards</h2>

        {/* Mark Filter Buttons */}
        <div
          className="filter-buttons"
          style={{
            display: 'flex',
            gap: '10px',
            marginBottom: '1rem',
            flexWrap: 'wrap',
          }}
        >
          <button onClick={() => handleFilter(1)}>1 Mark ({countByMarks(1)})</button>
          <button onClick={() => handleFilter(2)}>2 Marks ({countByMarks(2)})</button>
          <button onClick={() => handleFilter(5)}>5 Marks ({countByMarks(5)})</button>
          <button onClick={() => handleFilter(10)}>10 Marks ({countByMarks(10)})</button>
          <button
            onClick={() => {
              setSelectedMark(null);
              setFilteredCards(selectedUpload?.cards || []);
            }}
          >
            All ({selectedUpload?.cards.length || 0})
          </button>
        </div>

        {/* Flashcards List */}
        <div
          className="card-list"
          style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}
        >
          {filteredCards.length === 0 && <p>No flashcards for this selection.</p>}
          {filteredCards.map((card, idx) => (
            <div
              key={idx}
              className="flashcard"
              style={{
                border: '1px solid #ddd',
                borderRadius: '5px',
                padding: '1rem',
                backgroundColor: '#f0f8ff', // slightly lighter
              }}
            >
              <div className="question">
                <strong>Q:</strong> {card.question}
              </div>
              <div className="answer">
                <strong>A:</strong> {card.answer}
              </div>
              <div className="marks">🎯 Marks: {card.marks}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ReviewPage;
