import React, { useEffect, useState } from "react";
import "./ReviewPage.css";

const ReviewPage = () => {
  const [history, setHistory] = useState([]);
  const [selectedUpload, setSelectedUpload] = useState(null);
  const [flashcards, setFlashcards] = useState([]);
  const [filteredCards, setFilteredCards] = useState([]);
  const [selectedMark, setSelectedMark] = useState(null);

  const userId = localStorage.getItem("userId"); // make sure you save this on login

  // âœ… Fetch upload history
  useEffect(() => {
    const fetchHistory = async () => {
      try {
        const res = await fetch("/api/upload/history", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        const data = await res.json();
        setHistory(data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchHistory();
  }, []);

  // âœ… Fetch flashcards for selected upload
  useEffect(() => {
    if (!selectedUpload) return;

    const fetchFlashcards = async () => {
      try {
        const res = await fetch(`/api/upload/byUpload/${selectedUpload._id}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        const data = await res.json();
        setFlashcards(data);
        setFilteredCards(data); // initially show all
      } catch (err) {
        console.error(err);
      }
    };
    fetchFlashcards();
    setSelectedMark(null); // reset mark filter
  }, [selectedUpload]);

  const handleFilter = (marks) => {
    setSelectedMark(marks);
    setFilteredCards(flashcards.filter((c) => c.marks === marks));
  };

  const countByMarks = (marks) =>
    flashcards.filter((c) => c.marks === marks).length;

  return (
    <div className="review-page">
      {/* Left Sidebar: Upload History */}
      <div className="sidebar">
        <h3>ðŸ“‚ Upload History</h3>
        {history.length === 0 && <p>No uploads yet</p>}
        <ul>
          {history.map((upload) => (
            <li
              key={upload._id}
              className={selectedUpload?._id === upload._id ? "active" : ""}
              onClick={() => setSelectedUpload(upload)}
            >
              {upload.filename} ({new Date(upload.uploadedAt).toLocaleDateString()})
            </li>
          ))}
        </ul>
      </div>

      {/* Right Panel: Flashcards + Mark Filters */}
      <div className="review-container">
        <h2>ðŸ“š Review Flashcards</h2>

        {!selectedUpload && <p>Select an upload from history to review flashcards.</p>}

        {selectedUpload && (
          <>
            {/* Filter Buttons */}
            <div className="filter-buttons">
              <button onClick={() => handleFilter(1)}>1 Mark ({countByMarks(1)})</button>
              <button onClick={() => handleFilter(2)}>2 Marks ({countByMarks(2)})</button>
              <button onClick={() => handleFilter(5)}>5 Marks ({countByMarks(5)})</button>
              <button onClick={() => handleFilter(10)}>10 Marks ({countByMarks(10)})</button>
            </div>

            {/* Flashcards */}
            {selectedMark && filteredCards.length === 0 && (
              <p>No flashcards found for {selectedMark} Marks.</p>
            )}
            <div className="card-list">
              {(selectedMark ? filteredCards : flashcards).map((card, index) => (
                <div key={index} className="flashcard">
                  <div className="question">Q: {card.question}</div>
                  <div className="answer">A: {card.answer}</div>
                  <div className="marks">ðŸŽ¯ Marks: {card.marks || "N/A"}</div>
                  <div className="difficulty">âš¡ Difficulty: {card.difficulty}</div>
                </div>
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default ReviewPage;
