    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    pointerEvents: "none",
    zIndex: 0,
  },
  
  shape1: {
    position: "absolute",
    top: "10%",
    right: "15%",
    width: "200px",
    height: "200px",
    background: "rgba(255,255,255,0.05)",
    borderRadius: "50%",
    animation: "float 6s ease-in-out infinite",
  },
  
  shape2: {
    position: "absolute",
    bottom: "20%",
    left: "10%",
    width: "150px",
    height: "150px",
    background: "rgba(255,255,255,0.03)",
    borderRadius: "30% 70% 70% 30% / 30% 30% 70% 70%",
    animation: "float 8s ease-in-out infinite reverse",
  },

  title: {
