    fontWeight: "700",
    textAlign: "center",
    letterSpacing: "1px",
    textShadow: "0 2px 10px rgba(0,0,0,0.2)",
    zIndex: 1,
    position: "relative",
  },

  cardBox: {
    background: "rgba(255, 255, 255, 0.95)",
    backdropFilter: "blur(20px)",
    border: "1px solid rgba(255, 255, 255, 0.2)",
    borderRadius: "24px",
