import React, { useEffect, useState } from "react";
import "./ReviewPage.css";
import axios from "axios";

// âœ… Sidebar component inside same file (can move to components/Sidebar.jsx if you want)
function Sidebar({ userId, onSelect }) {
  const [history, setHistory] = useState([]);

  useEffect(() => {
    if (userId) {
      axios.get(`/api/history/${userId}`).then((res) => setHistory(res.data));
    }
  }, [userId]);

  return (
    <div className="sidebar">
      <h3>Recent</h3>
      <ul>
        <li onClick={() => onSelect("today")}>Today</li>
        <li onClick={() => onSelect("yesterday")}>Yesterday</li>
      </ul>

      <h3>Search History</h3>
      <ul>
        {history.map((item, index) => (
          <li key={index} onClick={() => onSelect(item)}>
            {item.query}
          </li>
        ))}
      </ul>
    </div>
  );
}

const ReviewPage = () => {
  const userId = "USER_ID_HERE"; // ðŸ”¹ replace with actual logged-in user id
  const [flashcards, setFlashcards] = useState([]);
  const [filteredCards, setFilteredCards] = useState([]);
  const [selectedMark, setSelectedMark] = useState(null);

  const [historyResults, setHistoryResults] = useState([]); // ðŸ”¹ for sidebar results

  useEffect(() => {
    // âœ… Load flashcards from localStorage (default data)
    const savedCards = localStorage.getItem("flashcards");
    if (savedCards) {
      const parsed = JSON.parse(savedCards);
      setFlashcards(parsed);
    }
  }, []);

  const handleFilter = (marks) => {
    setSelectedMark(marks);
    setFilteredCards(flashcards.filter((c) => c.marks === marks));
  };

  // âœ… Sidebar item click
  const handleSelect = async (item) => {
    if (item === "today") {
      const res = await axios.get(`/api/history/today/${userId}`);
      setHistoryResults(res.data);
    } else if (item === "yesterday") {
      const res = await axios.get(`/api/history/yesterday/${userId}`);
      setHistoryResults(res.data);
    } else {
      setHistoryResults([item]); // specific history item
    }
    setSelectedMark(null); // clear mark filter view
  };

  // âœ… Count flashcards per mark
  const countByMarks = (marks) =>
    flashcards.filter((c) => c.marks === marks).length;

  return (
    <div className="review-wrapper">
      {/* Sidebar */}
      <Sidebar userId={userId} onSelect={handleSelect} />

      {/* Main content */}
      <div className="review-container">
        <h2>ðŸ“š Review Flashcards</h2>

        {/* âœ… If showing history results */}
        {historyResults.length > 0 && (
          <div className="history-results">
            <h3>ðŸ“œ History Results</h3>
            {historyResults.map((h, i) => (
              <div key={i} className="flashcard">
                <div className="question">Q: {h.result?.question}</div>
                <div className="answer">A: {h.result?.answer}</div>
                <div className="marks">ðŸŽ¯ Marks: {h.result?.marks}</div>
              </div>
            ))}
          </div>
        )}

        {/* âœ… If no history selected â†’ show filter options */}
        {historyResults.length === 0 && (
          <>
            <div className="filter-buttons">
              <button onClick={() => handleFilter(1)}>
                1 Mark ({countByMarks(1)})
              </button>
              <button onClick={() => handleFilter(2)}>
                2 Marks ({countByMarks(2)})
              </button>
              <button onClick={() => handleFilter(5)}>
                5 Marks ({countByMarks(5)})
              </button>
              <button onClick={() => handleFilter(10)}>
                10 Marks ({countByMarks(10)})
              </button>
            </div>

            {!selectedMark && (
              <p>Please select a mark type to review questions.</p>
            )}

            {selectedMark && filteredCards.length === 0 && (
              <p>No flashcards found for {selectedMark} Marks.</p>
            )}

            {selectedMark && filteredCards.length > 0 && (
              <div className="card-list">
                {filteredCards.map((card, index) => (
                  <div key={index} className="flashcard">
                    <div className="question">Q: {card.question}</div>
                    <div className="answer">A: {card.answer}</div>
                    <div className="marks">ðŸŽ¯ Marks: {card.marks}</div>
                  </div>
                ))}
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default ReviewPage;
