import React, { useEffect, useState } from "react";
import "./ReviewPage.css";

const ReviewPage = () => {
  const [uploadsHistory, setUploadsHistory] = useState([]);
  const [selectedUpload, setSelectedUpload] = useState(null);
  const [filteredQuestions, setFilteredQuestions] = useState([]);

  useEffect(() => {
    // LocalStorage la store pannirukura history fetch pannanum
    const storedHistory = JSON.parse(localStorage.getItem("uploadsHistory")) || [];
    setUploadsHistory(storedHistory);

    // First item default-a select pannanum
    if (storedHistory.length > 0) {
      setSelectedUpload(storedHistory[0]);
      setFilteredQuestions(storedHistory[0].flashcards);
    }
  }, []);

  const handleSelectUpload = (upload) => {
    setSelectedUpload(upload);
    setFilteredQuestions(upload.flashcards);
  };

  const filterByMarks = (marks) => {
    if (!selectedUpload) return;
    // Just demo: question length-based filter (later, add "marks" field in flashcards)
    const filtered = selectedUpload.flashcards.filter(
      (q) => q.marks === marks
    );
    setFilteredQuestions(filtered);
  };

  return (
    <div className="review-container">
      {/* Left Sidebar */}
      <div className="sidebar">
        <h3>ðŸ“‚ History</h3>
        <ul>
          {uploadsHistory.map((upload) => (
            <li
              key={upload.id}
              className={selectedUpload?.id === upload.id ? "active" : ""}
              onClick={() => handleSelectUpload(upload)}
            >
              {upload.title} <br />
              <small>{upload.date}</small>
            </li>
          ))}
        </ul>
      </div>

      {/* Right Review Section */}
      <div className="review-content">
        {selectedUpload ? (
          <>
            <h2>{selectedUpload.title} - Review</h2>

            {/* Buttons for marks filter */}
            <div className="filter-buttons">
              <button onClick={() => filterByMarks(1)}>1 Mark</button>
              <button onClick={() => filterByMarks(2)}>2 Mark</button>
              <button onClick={() => filterByMarks(5)}>5 Mark</button>
              <button onClick={() => filterByMarks(10)}>10 Mark</button>
            </div>

            {/* Questions Review */}
            <ul className="question-list">
              {filteredQuestions.length > 0 ? (
                filteredQuestions.map((q, idx) => (
                  <li key={idx}>
                    <strong>Q{idx + 1}: {q.question}</strong>
                    <br />
                    âœ… Answer: {q.answer}
                    <br />
                    <em>({q.marks} marks)</em>
                  </li>
                ))
              ) : (
                <p>No questions available for this filter.</p>
              )}
            </ul>
          </>
        ) : (
          <p>No uploads found.</p>
        )}
      </div>
    </div>
  );
};

export default ReviewPage;
