import React, { useEffect, useState } from "react";

const QnSpeakPage = () => {
  const [flashcards, setFlashcards] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);
  const [difficultyProgress, setDifficultyProgress] = useState({
    easy: 0,
    medium: 0,
    hard: 0,
  });
  const [totalByDifficulty, setTotalByDifficulty] = useState({
    easy: 0,
    medium: 0,
    hard: 0,
  });
  const [shownByDifficulty, setShownByDifficulty] = useState({
    easy: 0,
    medium: 0,
    hard: 0,
  });

  // Load flashcards and calculate totals
  const loadFlashcards = () => {
    const cards = JSON.parse(localStorage.getItem("flashcards")) || [];
    const normalizedCards = cards.map((c) => ({
      ...c,
      difficulty: c.difficulty ? c.difficulty.toLowerCase() : "easy",
    }));
    setFlashcards(normalizedCards);

    const totals = { easy: 0, medium: 0, hard: 0 };
    normalizedCards.forEach((c) => {
      if (totals[c.difficulty] !== undefined) totals[c.difficulty]++;
    });
    setTotalByDifficulty(totals);

    setShownByDifficulty({ easy: 0, medium: 0, hard: 0 });
    setDifficultyProgress({ easy: 0, medium: 0, hard: 0 });
    setCurrentIndex(0);

    if (normalizedCards.length > 0) incrementProgress(normalizedCards[0].difficulty);
  };

  useEffect(() => {
    loadFlashcards();
    window.addEventListener("storage", loadFlashcards);
    return () => window.removeEventListener("storage", loadFlashcards);
  }, []);

  const currentCard = flashcards[currentIndex];

  const incrementProgress = (level) => {
    if (!level || !totalByDifficulty[level]) return;

    setShownByDifficulty((prev) => {
      const updatedShown = { ...prev };
      updatedShown[level] = Math.min(
        (updatedShown[level] || 0) + 1,
        totalByDifficulty[level]
      );

      setDifficultyProgress((prevProgress) => {
        const updatedProgress = { ...prevProgress };
        updatedProgress[level] = Math.round(
          (updatedShown[level] / totalByDifficulty[level]) * 100
        );
        return updatedProgress;
      });

      return updatedShown;
    });
  };

  const speakAnswer = () => {
    if (!currentCard) return;
    if ("speechSynthesis" in window) {
      window.speechSynthesis.cancel();
      const utterance = new SpeechSynthesisUtterance(currentCard.answer);
      utterance.lang = "en-US";
      window.speechSynthesis.speak(utterance);
      setShowAnswer(true);
    }
  };

  const stopSpeech = () => {
    if ("speechSynthesis" in window) window.speechSynthesis.cancel();
  };

  const nextQuestion = () => {
    stopSpeech();
    setShowAnswer(false);
    if (currentIndex < flashcards.length - 1) {
      const nextIdx = currentIndex + 1;
      setCurrentIndex(nextIdx);
      incrementProgress(flashcards[nextIdx].difficulty);
    } else {
      alert("üéâ You have completed all flashcards!");
      setCurrentIndex(0);
    }
  };

  if (flashcards.length === 0) {
    return <p style={styles.noFlashcards}>No flashcards available.</p>;
  }

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>üìö QnSpeak Flashcards</h2>

      <div style={styles.cardBox}>
        <div style={styles.question}><strong>Q:</strong> {currentCard.question}</div>

        {showAnswer && (
          <div style={styles.answer}><strong>A:</strong> {currentCard.answer}</div>
        )}

        <div style={styles.buttons}>
          <button style={styles.speakBtn} onClick={speakAnswer}>üé§ Voice Answer</button>
          <button style={styles.stopBtn} onClick={stopSpeech}>‚èπ Stop</button>
          <button style={styles.nextBtn} onClick={nextQuestion}>‚û° Next</button>
        </div>

        <div style={styles.progressContainer}>
          <h4>Progress by Difficulty:</h4>
          {["easy", "medium", "hard"].map((level) => (
            <div key={level} style={styles.progressRow}>
              <strong>{level.charAt(0).toUpperCase() + level.slice(1)}</strong>
              <div style={styles.progressBarBg}>
                <div
                  style={{
                    ...styles.progressBarFill,
                    width: `${difficultyProgress[level]}%`,
                    backgroundColor: level === "easy" ? "#4caf50" : level === "medium" ? "#ff9800" : "#f44336",
                  }}
                ></div>
              </div>
              <span style={styles.progressText}>{difficultyProgress[level]}%</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    minHeight: "100vh",
    flexDirection: "column",
    backgroundColor: "#e0f7fa",
    fontFamily: "Arial, sans-serif",
    padding: "2rem",
  },
  title: { marginBottom: "20px", color: "#00796b" },
  cardBox: {
    border: "1px solid #ccc",
    borderRadius: "10px",
    padding: "2rem",
    width: "450px",
    backgroundColor: "#ffffff",
    boxShadow: "0 6px 12px rgba(0,0,0,0.1)",
    textAlign: "center",
  },
  question: { marginBottom: "1rem", fontSize: "16px" },
  answer: { marginBottom: "1rem", color: "#007bff", fontSize: "16px" },
  buttons: { display: "flex", justifyContent: "center", gap: "10px", marginBottom: "1rem", flexWrap: "wrap" },
  speakBtn: { padding: "8px 16px", borderRadius: "8px", backgroundColor: "#4caf50", color: "white", border: "none", cursor: "pointer" },
  stopBtn: { padding: "8px 16px", borderRadius: "8px", backgroundColor: "#f44336", color: "white", border: "none", cursor: "pointer" },
  nextBtn: { padding: "8px 16px", borderRadius: "8px", backgroundColor: "#2196f3", color: "white", border: "none", cursor: "pointer" },
  progressContainer: { marginTop: "1.5rem", textAlign: "left" },
  progressRow: { marginBottom: "0.5rem", display: "flex", alignItems: "center" },
  progressBarBg: { display: "inline-block", width: "250px", height: "10px", backgroundColor: "#ddd", borderRadius: "5px", marginLeft: "10px", overflow: "hidden" },
  progressBarFill: { height: "100%", transition: "width 0.3s" },
  progressText: { marginLeft: "0.5rem" },
  noFlashcards: { textAlign: "center", marginTop: "2rem", fontSize: "18px" },
};

export default QnSpeakPage;
