import React, { useEffect, useState } from "react";
import "./QnSpeakPage.css"; // Optional: for styling progress bars, center alignment

const QnSpeakPage = () => {
  const [flashcards, setFlashcards] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);
  const [difficultyProgress, setDifficultyProgress] = useState({
    easy: 0,
    medium: 0,
    hard: 0,
  });

  useEffect(() => {
    const cards = JSON.parse(localStorage.getItem("flashcards")) || [];
    setFlashcards(cards);
  }, []);

  if (flashcards.length === 0) {
    return <p style={{ textAlign: "center", marginTop: "2rem" }}>No flashcards available.</p>;
  }

  const currentCard = flashcards[currentIndex];

  const speakAnswer = () => {
    if ("speechSynthesis" in window) {
      window.speechSynthesis.cancel(); // stop previous speech
      const utterance = new SpeechSynthesisUtterance(currentCard.answer);
      utterance.lang = "en-US";
      utterance.rate = 1;
      utterance.pitch = 1;
      window.speechSynthesis.speak(utterance);
      setShowAnswer(true);
    }
  };

  const stopSpeech = () => {
    if ("speechSynthesis" in window) {
      window.speechSynthesis.cancel();
    }
  };

  const nextQuestion = () => {
    stopSpeech();
    setShowAnswer(false);
    if (currentIndex < flashcards.length - 1) {
      setCurrentIndex(currentIndex + 1);
    } else {
      alert("üéâ You have completed all flashcards!");
      setCurrentIndex(0);
    }
  };

  const handleDifficulty = (level) => {
    setDifficultyProgress((prev) => {
      const updated = { ...prev };
      updated[level] = Math.min(100, updated[level] + 10); // increment by 10% max 100%
      return updated;
    });
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        minHeight: "100vh",
        flexDirection: "column",
        backgroundColor: "#f0f4f8",
        padding: "2rem",
      }}
    >
      <h2>üìö QnSpeak Flashcards</h2>

      {/* Card */}
      <div
        style={{
          border: "1px solid #ccc",
          borderRadius: "10px",
          padding: "2rem",
          width: "400px",
          backgroundColor: "#fff",
          boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
          marginTop: "2rem",
        }}
      >
        <div style={{ marginBottom: "1rem" }}>
          <strong>Q:</strong> {currentCard.question}
        </div>

        {/* Answer */}
        {showAnswer && (
          <div style={{ marginBottom: "1rem", color: "#007bff" }}>
            <strong>A:</strong> {currentCard.answer}
          </div>
        )}

        {/* Buttons */}
        <div style={{ display: "flex", gap: "10px", flexWrap: "wrap" }}>
          <button onClick={speakAnswer}>üé§ Voice Answer</button>
          <button onClick={stopSpeech}>‚èπ Stop</button>
          <button onClick={nextQuestion}>‚û° Next</button>
        </div>

        {/* Difficulty buttons */}
        <div style={{ marginTop: "1.5rem" }}>
          <h4>Select Difficulty:</h4>
          <div style={{ display: "flex", flexDirection: "column", gap: "0.5rem" }}>
            {["easy", "medium", "hard"].map((level) => (
              <div key={level}>
                <button onClick={() => handleDifficulty(level)} style={{ marginRight: "0.5rem" }}>
                  {level.charAt(0).toUpperCase() + level.slice(1)}
                </button>
                <div
                  style={{
                    display: "inline-block",
                    width: "200px",
                    height: "10px",
                    backgroundColor: "#ddd",
                    borderRadius: "5px",
                    overflow: "hidden",
                    verticalAlign: "middle",
                  }}
                >
                  <div
                    style={{
                      width: `${difficultyProgress[level]}%`,
                      height: "100%",
                      backgroundColor: level === "easy" ? "#4caf50" : level === "medium" ? "#ff9800" : "#f44336",
                      transition: "width 0.3s",
                    }}
                  ></div>
                </div>
                <span style={{ marginLeft: "0.5rem" }}>{difficultyProgress[level]}%</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default QnSpeakPage;
