import React, { useEffect, useState } from "react";

const ReviewPage = () => {
  const [uploads, setUploads] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/uploads")
      .then((res) => res.json())
      .then((data) => setUploads(data));
  }, []);

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className="w-64 bg-gray-100 p-4 border-r overflow-y-auto">
        <h2 className="text-xl font-bold mb-4">ðŸ•˜ Search History</h2>
        {uploads.length === 0 ? (
          <p className="text-gray-500">No uploads yet</p>
        ) : (
          <ul className="space-y-2">
            {uploads.map((file) => (
              <li
                key={file._id}
                className="p-2 cursor-pointer hover:bg-gray-200 rounded"
                onClick={() => setSelectedFile(file)}
              >
                ðŸ“„ {file.filename}{" "}
                <span className="text-sm text-gray-400">
                  ({new Date(file.uploadDate).toLocaleDateString()})
                </span>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Main Review Area */}
      <div className="flex-1 p-6">
        {selectedFile ? (
          <div>
            <h2 className="text-2xl font-bold mb-4">{selectedFile.filename}</h2>
            <ul className="space-y-2">
              {selectedFile.questions.map((q, idx) => (
                <li key={idx} className="p-3 border rounded bg-white">
                  <strong>Q:</strong> {q.question} <br />
                  <strong>A:</strong> {q.answer}
                </li>
              ))}
            </ul>
          </div>
        ) : (
          <p className="text-gray-500">Select a file to review its questions</p>
        )}
      </div>
    </div>
  );
};

export default ReviewPage;
