import React, { useEffect, useState } from "react";

const QnSpeakPage = () => {
  const [flashcards, setFlashcards] = useState([]);

  useEffect(() => {
    // Load flashcards from localStorage or fetch from backend
    const cards = JSON.parse(localStorage.getItem("flashcards")) || [];
    setFlashcards(cards);
  }, []);

  const speakAnswer = (answer) => {
    if ("speechSynthesis" in window) {
      const utterance = new SpeechSynthesisUtterance(answer);
      utterance.lang = "en-US";
      utterance.rate = 1; // speed
      utterance.pitch = 1; // pitch
      window.speechSynthesis.speak(utterance);
    } else {
      alert("‚ùå Sorry, your browser does not support speech synthesis.");
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>üé§ QnSpeak Flashcards</h2>
      {flashcards.length === 0 && <p>No flashcards found.</p>}
      <div style={{ display: "flex", flexDirection: "column", gap: "1rem" }}>
        {flashcards.map((card, idx) => (
          <div
            key={idx}
            style={{
              border: "1px solid #ddd",
              padding: "1rem",
              borderRadius: "5px",
              backgroundColor: "#f9f9f9",
            }}
          >
            <div>
              <strong>Q:</strong> {card.question}
            </div>
            <div>
              <strong>A:</strong> {card.answer}
            </div>
            <button
              style={{ marginTop: "0.5rem", cursor: "pointer" }}
              onClick={() => speakAnswer(card.answer)}
            >
              üîä Speak Answer
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default QnSpeakPage;
