import React, { useEffect, useState } from "react";
import "./QnSpeakPage.css";

const QnSpeakPage = () => {
  const [flashcards, setFlashcards] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);
  const [difficultyProgress, setDifficultyProgress] = useState({
    easy: 0,
    medium: 0,
    hard: 0,
  });

  useEffect(() => {
    const cards = JSON.parse(localStorage.getItem("flashcards")) || [];
    setFlashcards(cards);

    // Automatically increment first card's difficulty progress
    if (cards.length > 0) {
      incrementDifficultyProgress(cards[0].difficulty);
    }
  }, []);

  const currentCard = flashcards[currentIndex];

  const incrementDifficultyProgress = (level) => {
    if (!level) return;
    setDifficultyProgress((prev) => {
      const updated = { ...prev };
      updated[level] = Math.min(100, prev[level] + 10); // +10% per view
      return updated;
    });
  };

  const speakAnswer = () => {
    if ("speechSynthesis" in window) {
      window.speechSynthesis.cancel();
      const utterance = new SpeechSynthesisUtterance(currentCard.answer);
      utterance.lang = "en-US";
      window.speechSynthesis.speak(utterance);
      setShowAnswer(true);
    }
  };

  const stopSpeech = () => {
    if ("speechSynthesis" in window) {
      window.speechSynthesis.cancel();
    }
  };

  const nextQuestion = () => {
    stopSpeech();
    setShowAnswer(false);
    if (currentIndex < flashcards.length - 1) {
      const nextIndex = currentIndex + 1;
      setCurrentIndex(nextIndex);
      // Auto-increment progress for next card
      incrementDifficultyProgress(flashcards[nextIndex].difficulty);
    } else {
      alert("🎉 You have completed all flashcards!");
      setCurrentIndex(0);
      // Optional: reset progress or keep as is
    }
  };

  if (flashcards.length === 0) {
    return <p style={{ textAlign: "center", marginTop: "2rem" }}>No flashcards available.</p>;
  }

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        minHeight: "100vh",
        flexDirection: "column",
        backgroundColor: "#f0f4f8",
        padding: "2rem",
      }}
    >
      <h2>📚 QnSpeak Flashcards</h2>

      {/* Card */}
      <div
        style={{
          border: "1px solid #ccc",
          borderRadius: "10px",
          padding: "2rem",
          width: "400px",
          backgroundColor: "#fff",
          boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
          marginTop: "2rem",
        }}
      >
        <div style={{ marginBottom: "1rem" }}>
          <strong>Q:</strong> {currentCard.question}
        </div>

        {showAnswer && (
          <div style={{ marginBottom: "1rem", color: "#007bff" }}>
            <strong>A:</strong> {currentCard.answer}
          </div>
        )}

        {/* Buttons */}
        <div style={{ display: "flex", gap: "10px", flexWrap: "wrap" }}>
          <button onClick={speakAnswer}>🎤 Voice Answer</button>
          <button onClick={stopSpeech}>⏹ Stop</button>
          <button onClick={nextQuestion}>➡ Next</button>
        </div>

        {/* Difficulty Progress */}
        <div style={{ marginTop: "1.5rem" }}>
          <h4>Progress by Difficulty:</h4>
          {["easy", "medium", "hard"].map((level) => (
            <div key={level} style={{ marginBottom: "0.5rem" }}>
              <strong>{level.charAt(0).toUpperCase() + level.slice(1)}</strong>
              <div
                style={{
                  display: "inline-block",
                  width: "200px",
                  height: "10px",
                  backgroundColor: "#ddd",
                  borderRadius: "5px",
                  marginLeft: "10px",
                  overflow: "hidden",
                  verticalAlign: "middle",
                }}
              >
                <div
                  style={{
                    width: `${difficultyProgress[level]}%`,
                    height: "100%",
                    backgroundColor:
                      level === "easy" ? "#4caf50" : level === "medium" ? "#ff9800" : "#f44336",
                    transition: "width 0.3s",
                  }}
                ></div>
              </div>
              <span style={{ marginLeft: "0.5rem" }}>{difficultyProgress[level]}%</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default QnSpeakPage;
