import React, { useEffect, useState, useMemo } from "react";
import { useNavigate } from "react-router-dom";

const QnSpeakPage = () => {
  const [cards, setCards] = useState([]);
  const [selected, setSelected] = useState("Easy");
  const [idx, setIdx] = useState(0);
  const [done, setDone] = useState({
    Easy: new Set(),
    Medium: new Set(),
    Hard: new Set(),
  });

  const navigate = useNavigate();

  // Load flashcards and initialize
  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("flashcards") || "[]");
    setCards(saved);
    setIdx(0);
    setDone({ Easy: new Set(), Medium: new Set(), Hard: new Set() });
  }, []);

  const filtered = useMemo(
    () => cards.filter((c) => (c.difficulty || "Easy") === selected),
    [cards, selected]
  );

  const current = filtered[idx] || null;
  const totalInCat = filtered.length;

  // Automatically mark current question as done for progress
  useEffect(() => {
    if (!current) return;
    const id = current._id || current.id || `${selected}-${idx}`;
    setDone((prev) => {
      const copy = {
        Easy: new Set(prev.Easy),
        Medium: new Set(prev.Medium),
        Hard: new Set(prev.Hard),
      };
      copy[selected].add(id); // mark current question as done
      return copy;
    });
  }, [current, selected, idx]);

  const speakAnswer = () => {
    if (!current) return;
    const u = new SpeechSynthesisUtterance(current.answer || "");
    window.speechSynthesis.cancel();
    window.speechSynthesis.speak(u);
  };

  const stopSpeech = () => {
    if ("speechSynthesis" in window) {
      window.speechSynthesis.cancel();
    }
  };

  const nextQuestion = () => {
    stopSpeech();
    if (idx < totalInCat - 1) {
      setIdx(idx + 1);
    } else {
      alert(`‚úÖ You have completed all ${selected} questions!`);
      setIdx(0);
    }
  };

  const progressPct = (level) => {
    const total = cards.filter((c) => (c.difficulty || "Easy") === level).length;
    if (!total) return 0;
    return Math.min((done[level].size / total) * 100, 100);
  };

  if (cards.length === 0) {
    return <p style={{ textAlign: "center", marginTop: "2rem" }}>No flashcards available.</p>;
  }

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>üìö Test Mode: Hear the Answer</h2>

      <div style={styles.btnGroup}>
        {["Easy", "Medium", "Hard"].map((lvl) => (
          <button
            key={lvl}
            style={styles.filterBtn}
            onClick={() => { setSelected(lvl); setIdx(0); }}
          >
            {lvl}
          </button>
        ))}
      </div>

      <div style={styles.progressBox}>
        {["Easy", "Medium", "Hard"].map((lvl) => (
          <div key={lvl} style={{ marginBottom: 8 }}>
            <strong>{lvl}:</strong> {progressPct(lvl).toFixed(0)}%
            <progress value={progressPct(lvl)} max={100} style={styles.progress} />
          </div>
        ))}
      </div>

      {totalInCat === 0 ? (
        <div>No questions for {selected}. Upload a PDF or try another filter.</div>
      ) : (
        current && (
          <div style={styles.card}>
            <div style={{ marginBottom: 8 }}>
              <strong>Q:</strong> {current.question}
            </div>
            <button style={styles.speakBtn} onClick={speakAnswer}>
              üé§ Hear the Answer
            </button>
            <button style={{ ...styles.speakBtn, marginLeft: 10 }} onClick={stopSpeech}>
              ‚èπ Stop
            </button>
            <div style={{ marginTop: 12 }}>
              <button style={styles.nextBtn} onClick={nextQuestion}>
                Next ‚û°Ô∏è
              </button>
              <span style={{ marginLeft: 10 }}>
                {done[selected].size} / {totalInCat} in {selected}
              </span>
            </div>
          </div>
        )
      )}

      <div style={styles.navButtons}>
        <button onClick={() => navigate("/review")} style={styles.navBtn}>
          ‚¨ÖÔ∏è Back to Review
        </button>
        <button onClick={() => navigate("/")} style={styles.navBtn}>
          üè† Home
        </button>
      </div>
    </div>
  );
};

const styles = {
  container: { textAlign: "center", padding: "30px", fontFamily: "Arial, sans-serif" },
  title: { marginBottom: "20px", color: "#2c3e50" },
  btnGroup: { display: "flex", justifyContent: "center", gap: "10px", marginBottom: "20px" },
  filterBtn: { padding: "8px 14px", borderRadius: "8px", border: "1px solid #888", background: "#f0f0f0", cursor: "pointer" },
  progressBox: { marginBottom: "20px", textAlign: "left", display: "inline-block" },
  progress: { width: "260px", display: "block", marginBottom: "10px" },
  card: { margin: "0 auto", maxWidth: "500px", padding: "20px", border: "1px solid #ddd", borderRadius: "12px", background: "#f9f9f9", boxShadow: "0 4px 8px rgba(0,0,0,0.1)" },
  speakBtn: { padding: "10px 16px", borderRadius: "8px", background: "#3498db", color: "white", border: "none", cursor: "pointer" },
  nextBtn: { padding: "8px 14px", borderRadius: "8px", background: "#2ecc71", color: "white", border: "none", cursor: "pointer" },
  doneMsg: { color: "green", fontWeight: "bold", marginTop: "20px" },
  navButtons: { marginTop: "30px", display: "flex", justifyContent: "center", gap: "15px" },
  navBtn: { padding: "10px 16px", borderRadius: "8px", background: "#9b59b6", color: "white", border: "none", cursor: "pointer" },
};

export default QnSpeakPage;
