import React, { useEffect, useState, useMemo } from "react";
import { useNavigate } from "react-router-dom"; // ‚úÖ navigation

const QnSpeakPage = () => {
  const [cards, setCards] = useState([]);
  const [selected, setSelected] = useState("Easy");
  const [idx, setIdx] = useState(0);
  const [done, setDone] = useState({
    Easy: new Set(),
    Medium: new Set(),
    Hard: new Set(),
  });

  const navigate = useNavigate();

  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem("flashcards") || "[]");
    setCards(saved);
    setIdx(0);
    setDone({ Easy: new Set(), Medium: new Set(), Hard: new Set() });
  }, []);

  const filtered = useMemo(
    () => cards.filter((c) => (c.difficulty || "Easy") === selected),
    [cards, selected]
  );

  const current = filtered[idx] || null;
  const totalInCat = filtered.length;

  const speakAnswer = () => {
    if (!current) return;
    const u = new SpeechSynthesisUtterance(current.answer || "");
    window.speechSynthesis.cancel();
    window.speechSynthesis.speak(u);
  };

  const markAndNext = () => {
    if (!current) return;
    setDone((prev) => {
      const copy = {
        Easy: new Set(prev.Easy),
        Medium: new Set(prev.Medium),
        Hard: new Set(prev.Hard),
      };
      const id = current._id || current.id || `${selected}-${idx}`;
      copy[selected].add(id);
      return copy;
    });
    setIdx((i) => Math.min(i + 1, Math.max(totalInCat - 1, 0)));
  };

  const progressPct = (level) => {
    const total = cards.filter((c) => (c.difficulty || "Easy") === level).length;
    if (!total) return 0;
    return Math.min((done[level].size / total) * 100, 100);
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>üéß Test Mode: Hear the Answer</h2>

      <div style={styles.btnGroup}>
        <button style={styles.filterBtn} onClick={() => { setSelected("Easy"); setIdx(0); }}>Easy</button>
        <button style={styles.filterBtn} onClick={() => { setSelected("Medium"); setIdx(0); }}>Medium</button>
        <button style={styles.filterBtn} onClick={() => { setSelected("Hard"); setIdx(0); }}>Hard</button>
      </div>

      <div style={styles.progressBox}>
        <div>Easy: {progressPct("Easy").toFixed(0)}%</div>
        <progress value={progressPct("Easy")} max={100} style={styles.progress} />
        <div>Medium: {progressPct("Medium").toFixed(0)}%</div>
        <progress value={progressPct("Medium")} max={100} style={styles.progress} />
        <div>Hard: {progressPct("Hard").toFixed(0)}%</div>
        <progress value={progressPct("Hard")} max={100} style={styles.progress} />
      </div>

      {totalInCat === 0 ? (
        <div>No questions for {selected}. Upload a PDF or try another filter.</div>
      ) : done[selected].size >= totalInCat ? (
        <div style={styles.doneMsg}>‚úÖ {selected} category completed!</div>
      ) : (
        current && (
          <div style={styles.card}>
            <div style={{ marginBottom: 8 }}>
              <strong>Q:</strong> {current.question}
            </div>
            <button style={styles.speakBtn} onClick={speakAnswer}>üîä Hear the Answer</button>
            <div style={{ marginTop: 12 }}>
              <button style={styles.nextBtn} onClick={markAndNext}>Next ‚û°Ô∏è</button>
              <span style={{ marginLeft: 10 }}>
                {done[selected].size} / {totalInCat} in {selected}
              </span>
            </div>
          </div>
        )
      )}

      {/* ‚úÖ Navigation buttons */}
      <div style={styles.navButtons}>
        <button onClick={() => navigate("/review")} style={styles.navBtn}>‚¨ÖÔ∏è Back to Review</button>
        <button onClick={() => navigate("/")} style={styles.navBtn}>üè† Home</button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    textAlign: "center",
    padding: "30px",
    fontFamily: "Arial, sans-serif",
  },
  title: {
    marginBottom: "20px",
    color: "#2c3e50",
  },
  btnGroup: {
    display: "flex",
    justifyContent: "center",
    gap: "10px",
    marginBottom: "20px",
  },
  filterBtn: {
    padding: "8px 14px",
    borderRadius: "8px",
    border: "1px solid #888",
    background: "#f0f0f0",
    cursor: "pointer",
  },
  progressBox: {
    marginBottom: "20px",
    textAlign: "left",
    display: "inline-block",
  },
  progress: {
    width: "260px",
    display: "block",
    marginBottom: "10px",
  },
  card: {
    margin: "0 auto",
    maxWidth: "500px",
    padding: "20px",
    border: "1px solid #ddd",
    borderRadius: "12px",
    background: "#f9f9f9",
    boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
  },
  speakBtn: {
    padding: "10px 16px",
    borderRadius: "8px",
    background: "#3498db",
    color: "white",
    border: "none",
    cursor: "pointer",
  },
  nextBtn: {
    padding: "8px 14px",
    borderRadius: "8px",
    background: "#2ecc71",
    color: "white",
    border: "none",
    cursor: "pointer",
  },
  doneMsg: {
    color: "green",
    fontWeight: "bold",
    marginTop: "20px",
  },
  navButtons: {
    marginTop: "30px",
    display: "flex",
    justifyContent: "center",
    gap: "15px",
  },
  navBtn: {
    padding: "10px 16px",
    borderRadius: "8px",
    background: "#9b59b6",
    color: "white",
    border: "none",
    cursor: "pointer",
  },
};

export default QnSpeakPage;
