import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './InputPage.css';  // ✅ import CSS

const InputPage = () => {
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) return setMessage('❌ Please select a PDF file.');

    const formData = new FormData();
    formData.append('pdf', file);

    const userId = localStorage.getItem('userId') || '';
    formData.append('userId', userId);

    try {
      const res = await fetch('http://localhost:5000/api/flashcards/upload', {
        method: 'POST',
        body: formData,
      });
      const data = await res.json();

      if (!data?.flashcards || !Array.isArray(data.flashcards)) {
        setMessage('❌ Failed to generate flashcards');
        return;
      }

      // ✅ Reset old flashcards before saving new set
      localStorage.removeItem('flashcards');
      localStorage.removeItem('speak_done');
      localStorage.removeItem('review_marks');

      // ✅ Save latest flashcards
      localStorage.setItem('flashcards', JSON.stringify(data.flashcards));
      localStorage.setItem(
        'flashcards_meta',
        JSON.stringify({
          uploadedAt: Date.now(),
          count: data.flashcards.length,
        })
      );

      setMessage(`✅ Flashcards generated: ${data.flashcards.length}`);

      // ✅ Go to Review page automatically
      setTimeout(() => navigate('/review'), 1000);
    } catch (err) {
      console.error(err);
      setMessage('❌ Upload failed');
    }
  };

  return (
    <div className="input-container">
      <h2>Upload PDF to Generate Flashcards</h2>
      <form onSubmit={handleUpload} className="input-form">
        <input
          type="file"
          accept=".pdf"
          onChange={(e) => setFile(e.target.files[0] || null)}
        />
        <button type="submit">Upload</button>
      </form>
      <p className="upload-message">{message}</p>
    </div>
  );
};

export default InputPage;
