// client/src/pages/ReviewPage.jsx
import React, { useState, useEffect } from "react";

const ReviewPage = () => {
  const [history, setHistory] = useState([]); // uploaded files list
  const [selectedUpload, setSelectedUpload] = useState(null); // current upload
  const [flashcards, setFlashcards] = useState([]); // flashcards of selected upload
  const [answer, setAnswer] = useState("");

  // Fetch upload history from backend
  useEffect(() => {
    fetch("http://localhost:5000/api/upload/history") // adjust route if diff
      .then((res) => res.json())
      .then((data) => setHistory(data))
      .catch((err) => console.error("Error fetching history:", err));
  }, []);

  // When a history item is clicked, fetch its flashcards
  const loadUpload = (uploadId) => {
    setSelectedUpload(uploadId);
    fetch(`http://localhost:5000/api/flashcards/${uploadId}`)
      .then((res) => res.json())
      .then((data) => {
        setFlashcards(data);
        setAnswer(""); // reset answer
      })
      .catch((err) => console.error("Error fetching flashcards:", err));
  };

  // Show answer based on button (marks selection)
  const showAnswer = (marks) => {
    if (flashcards.length > 0) {
      setAnswer(`Answer for ${marks}-mark question: ${flashcards[0].answer}`);
    } else {
      setAnswer("No flashcards available.");
    }
  };

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* Sidebar for history */}
      <div
        style={{
          width: "250px",
          backgroundColor: "#f5f5f5",
          borderRight: "1px solid #ddd",
          padding: "10px",
          overflowY: "auto",
        }}
      >
        <h3>History</h3>
        {history.length === 0 ? (
          <p>No uploads yet</p>
        ) : (
          <ul style={{ listStyle: "none", padding: 0 }}>
            {history.map((item) => (
              <li
                key={item._id}
                onClick={() => loadUpload(item._id)}
                style={{
                  padding: "8px",
                  marginBottom: "5px",
                  backgroundColor:
                    selectedUpload === item._id ? "#ddd" : "#fff",
                  borderRadius: "5px",
                  cursor: "pointer",
                }}
              >
                {item.filename}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Main Review Section */}
      <div style={{ flex: 1, padding: "20px" }}>
        <h2>Review Flashcards</h2>
        {selectedUpload ? (
          <>
            <div>
              <button onClick={() => showAnswer(1)}>1 Mark</button>
              <button onClick={() => showAnswer(2)}>2 Marks</button>
              <button onClick={() => showAnswer(5)}>5 Marks</button>
              <button onClick={() => showAnswer(10)}>10 Marks</button>
            </div>
            <div style={{ marginTop: "20px" }}>
              <strong>{answer}</strong>
            </div>
          </>
        ) : (
          <p>Select an upload from history to start reviewing.</p>
        )}
      </div>
    </div>
  );
};

export default ReviewPage;
