import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ReviewPage.css";

const ReviewPage = () => {
  const [flashcards, setFlashcards] = useState([]);
  const [filteredCards, setFilteredCards] = useState([]);
  const [selectedMark, setSelectedMark] = useState(null);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    // ✅ Load flashcards from localStorage
    const savedCards = localStorage.getItem("flashcards");
    if (savedCards) {
      const parsed = JSON.parse(savedCards);
      setFlashcards(parsed);
    }

    // ✅ Load history from backend
    const userId = "66f8a1234567890abcdef123"; // 👉 replace with actual logged-in userId
    axios
      .get(`http://localhost:5000/api/history/user/${userId}`)
      .then((res) => {
        console.log("✅ API response:", res.data);

        // ✅ If response is object with {history: [...]}
        if (res.data.history && Array.isArray(res.data.history)) {
          setHistory(res.data.history);
        }
        // ✅ If response is already array
        else if (Array.isArray(res.data)) {
          setHistory(res.data);
        }
        // ✅ If single object
        else {
          setHistory([res.data]);
        }
      })
      .catch((err) => {
        console.error("❌ Error fetching history:", err);
      });
  }, []);

  const handleFilter = (marks) => {
    setSelectedMark(marks);
    setFilteredCards(flashcards.filter((c) => c.marks === marks));
  };

  // ✅ Count flashcards per mark
  const countByMarks = (marks) =>
    flashcards.filter((c) => c.marks === marks).length;

  return (
    <div className="review-container">
      <h2>📚 Review Flashcards</h2>

      {/* ✅ History Sidebar */}
      <div className="history-section">
        <h3>🕘 Your Search History</h3>
        {Array.isArray(history) && history.length > 0 ? (
          <ul>
            {history.map((item) => (
              <li key={item._id || item.query}>
                <strong>{item.query}</strong> → {item.result}
              </li>
            ))}
          </ul>
        ) : (
          <p>No history found.</p>
        )}
      </div>

      {/* ✅ Mark filter buttons */}
      <div className="filter-buttons">
        <button onClick={() => handleFilter(1)}>
          1 Mark ({countByMarks(1)})
        </button>
        <button onClick={() => handleFilter(2)}>
          2 Marks ({countByMarks(2)})
        </button>
        <button onClick={() => handleFilter(5)}>
          5 Marks ({countByMarks(5)})
        </button>
        <button onClick={() => handleFilter(10)}>
          10 Marks ({countByMarks(10)})
        </button>
      </div>

      {/* ✅ If no filter selected */}
      {!selectedMark && <p>Please select a mark type to review questions.</p>}

      {/* ✅ Show selected category */}
      {selectedMark && filteredCards.length === 0 && (
        <p>No flashcards found for {selectedMark} Marks.</p>
      )}

      {selectedMark && filteredCards.length > 0 && (
        <div className="card-list">
          {filteredCards.map((card, index) => (
            <div key={index} className="flashcard">
              <div className="question">Q: {card.question}</div>
              <div className="answer">A: {card.answer}</div>
              <div className="marks">🎯 Marks: {card.marks}</div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ReviewPage;
