import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './HomePage.css';

const HomePage = () => {
  const [apiMessage, setApiMessage] = useState('');

  const testAPI = async () => {
    try {
      const res = await fetch('/apitest');
      if (!res.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await res.json();
      setApiMessage(`‚úÖ ${data.message}`); // use message instead of status
    } catch (err) {
      console.error(err);
      setApiMessage('‚ùå API test failed');
    }
  };

  return (
    <div
      className="home-container"
      style={{
        backgroundImage: 'url(/images/home-main.jpg)',
      }}
    >
      <h1 className="home-title">Welcome to Flashcard Generator üìö</h1>
      <p className="home-subtitle">
        Create and review flashcards from your study PDFs!
      </p>

      <Link to="/signup">
        <button className="home-btn">Get Started</button>
      </Link>

      <br />
      <button className="home-btn" onClick={testAPI}>
        Test Backend
      </button>

      {apiMessage && (
        <p style={{ marginTop: '20px', fontWeight: 'bold' }}>{apiMessage}</p>
      )}
    </div>
  );
};

export default HomePage;
