// App.js
import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import Navbar from './components/Navbar';
import HomePage from './pages/HomePage';
import SignupPage from './pages/SignupPage';
import LoginPage from './pages/LoginPage';
import InputPage from './pages/InputPage';
import ReviewPage from './pages/ReviewPage';
import QnSpeakPage from "./pages/QnSpeakPage";
import QuizPage from "./pages/QuizPage";

function App() {
  return (
    <BrowserRouter>
      <div style={{ display: "flex", height: "100vh" }}>
        {/* Sidebar */}
        <div
          style={{
            width: "220px",
            backgroundColor: "#f4f4f4",
            borderRight: "1px solid #ccc",
            padding: "20px",
          }}
        >
          <h3>📂 Menu</h3>
          <ul style={{ listStyle: "none", padding: 0 }}>
            <li style={{ padding: "10px" }}>
              <Link to="/">🏠 Home</Link>
            </li>
            <li style={{ padding: "10px" }}>
              <Link to="/signup">📝 Signup</Link>
            </li>
            <li style={{ padding: "10px" }}>
              <Link to="/login">🔑 Login</Link>
            </li>
            <li style={{ padding: "10px" }}>
              <Link to="/input">📤 Upload PDF</Link>
            </li>
            <li style={{ padding: "10px" }}>
              <Link to="/review">📚 Review Flashcards</Link>
            </li>
            <li style={{ padding: "10px" }}>
              <Link to="/qnspeak">🎤 Qn Speak</Link>
            </li>
            <li style={{ padding: "10px" }}>
              <Link to="/quiz">❓ Quiz Mode</Link>
            </li>
          </ul>
        </div>

        {/* Main Content */}
        <div style={{ flex: 1, padding: "20px" }}>
          <Navbar />
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/signup" element={<SignupPage />} />
            <Route path="/login" element={<LoginPage />} />
            <Route path="/input" element={<InputPage />} />
            <Route path="/review" element={<ReviewPage />} />
            <Route path="/qnspeak" element={<QnSpeakPage />} />
            <Route path="/quiz" element={<QuizPage />} />
          </Routes>
        </div>
      </div>
    </BrowserRouter>
  );
}

export default App;
