// client/src/pages/ReviewPage.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";

function ReviewPage() {
  const [flashcards, setFlashcards] = useState([]);
  const [filter, setFilter] = useState("All");

  useEffect(() => {
    const fetchFlashcards = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/flashcards");
        setFlashcards(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchFlashcards();
  }, []);

  // filter flashcards by mark
  const filteredFlashcards = flashcards.filter((card) => {
    if (filter === "All") return true;
    return card.mark === parseInt(filter);
  });

  // shorten answer based on mark type
  const formatAnswer = (answer, mark) => {
    if (mark === 1) {
      return answer.split(".")[0]; // only first line/keyword
    } else if (mark === 2) {
      return answer.split(".").slice(0, 3).join(". ") + ".";
    } else if (mark === 5) {
      return answer.split(".").slice(0, 8).join(". ") + ".";
    } else if (mark === 10) {
      return answer.split(".").slice(0, 15).join(". ") + ".";
    }
    return answer;
  };

  return (
    <div className="review-page">
      <h2>Review Flashcards</h2>

      {/* Filter Buttons */}
      <div className="filter-buttons">
        <button onClick={() => setFilter("All")}>All ({flashcards.length})</button>
        <button onClick={() => setFilter("1")}>
          1 Mark ({flashcards.filter((c) => c.mark === 1).length})
        </button>
        <button onClick={() => setFilter("2")}>
          2 Mark ({flashcards.filter((c) => c.mark === 2).length})
        </button>
        <button onClick={() => setFilter("5")}>
          5 Mark ({flashcards.filter((c) => c.mark === 5).length})
        </button>
        <button onClick={() => setFilter("10")}>
          10 Mark ({flashcards.filter((c) => c.mark === 10).length})
        </button>
      </div>

      {/* Flashcards */}
      <div className="flashcards">
        {filteredFlashcards.length === 0 ? (
          <p>No flashcards found for {filter}.</p>
        ) : (
          filteredFlashcards.map((card, index) => (
            <div key={index} className="flashcard">
              <h3>
                {card.mark}-Mark Q{index + 1}: {card.question}
              </h3>
              <p>{formatAnswer(card.answer, card.mark)}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default ReviewPage;
