// client/src/pages/ReviewPage.jsx
import React, { useEffect, useState } from "react";

const ReviewPage = () => {
  const [flashcards, setFlashcards] = useState([]);
  const [filter, setFilter] = useState("all");

  useEffect(() => {
    const fetchLatest = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/flashcards/latest");
        if (!res.ok) throw new Error("No flashcards found");
        const data = await res.json();
        setFlashcards(data.flashcards);
      } catch (err) {
        console.error(err);
      }
    };
    fetchLatest();
  }, []);

  const filteredFlashcards =
    filter === "all"
      ? flashcards
      : flashcards.filter((f) => f.marks === Number(filter));

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Review Flashcards</h2>

      <div className="flex gap-2 mb-4">
        <button onClick={() => setFilter("all")} className="bg-gray-500 text-white px-3 py-1 rounded">All</button>
        <button onClick={() => setFilter("1")} className="bg-green-500 text-white px-3 py-1 rounded">1 Mark</button>
        <button onClick={() => setFilter("2")} className="bg-blue-500 text-white px-3 py-1 rounded">2 Mark</button>
        <button onClick={() => setFilter("5")} className="bg-purple-500 text-white px-3 py-1 rounded">5 Mark</button>
        <button onClick={() => setFilter("10")} className="bg-red-500 text-white px-3 py-1 rounded">10 Mark</button>
      </div>

      {filteredFlashcards.length === 0 ? (
        <p>No flashcards available for this category.</p>
      ) : (
        filteredFlashcards.map((fc, idx) => (
          <div key={idx} className="border p-3 rounded mb-3 shadow">
            <p className="font-semibold">Q{idx + 1}: {fc.question}</p>
            <p className="text-gray-700 mt-1">Ans: {fc.answer}</p>
            <span className="text-sm text-gray-500">Marks: {fc.marks}</span>
          </div>
        ))
      )}
    </div>
  );
};

export default ReviewPage;
