import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ReviewPage.css";

const ReviewPage = () => {
  const [flashcards, setFlashcards] = useState([]);
  const [filteredCards, setFilteredCards] = useState([]);
  const [selectedMark, setSelectedMark] = useState(null);
  const [history, setHistory] = useState([]);

  const userId = "66f1abcd1234567890efgh12"; // âœ… Replace with logged in userId

  useEffect(() => {
    // âœ… Load flashcards from localStorage (latest upload)
    const savedCards = localStorage.getItem("flashcards");
    if (savedCards) {
      setFlashcards(JSON.parse(savedCards));
    }

    // âœ… Fetch history from backend
    const fetchHistory = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/history/${userId}`);
        setHistory(res.data);
      } catch (err) {
        console.error("History fetch error:", err);
      }
    };
    fetchHistory();
  }, []);

  const handleFilter = (marks) => {
    setSelectedMark(marks);
    setFilteredCards(flashcards.filter((c) => c.marks === marks));
  };

  const countByMarks = (marks) =>
    flashcards.filter((c) => c.marks === marks).length;

  // ðŸ“Œ Group history by Today, Yesterday, Earlier
  const groupHistory = () => {
    const today = new Date().toDateString();
    const yesterday = new Date(Date.now() - 86400000).toDateString();

    return {
      today: history.filter((h) => new Date(h.uploadedAt).toDateString() === today),
      yesterday: history.filter((h) => new Date(h.uploadedAt).toDateString() === yesterday),
      earlier: history.filter(
        (h) =>
          new Date(h.uploadedAt).toDateString() !== today &&
          new Date(h.uploadedAt).toDateString() !== yesterday
      ),
    };
  };

  const { today, yesterday, earlier } = groupHistory();

  return (
    <div style={{ display: "flex" }}>
      {/* ðŸ“Œ Sidebar */}
      <div className="sidebar">
        <h3>ðŸ•˜ Search History</h3>

        <div className="history-section">
          <h4>Today</h4>
          {today.length === 0 && <p>No uploads</p>}
          {today.map((h, i) => (
            <button key={i} onClick={() => alert(`Show flashcards for ${h.fileName}`)}>
              {h.fileName}
            </button>
          ))}
        </div>

        <div className="history-section">
          <h4>Yesterday</h4>
          {yesterday.length === 0 && <p>No uploads</p>}
          {yesterday.map((h, i) => (
            <button key={i} onClick={() => alert(`Show flashcards for ${h.fileName}`)}>
              {h.fileName}
            </button>
          ))}
        </div>

        <div className="history-section">
          <h4>Earlier</h4>
          {earlier.length === 0 && <p>No uploads</p>}
          {earlier.map((h, i) => (
            <button key={i} onClick={() => alert(`Show flashcards for ${h.fileName}`)}>
              {h.fileName}
            </button>
          ))}
        </div>
      </div>

      {/* ðŸ“Œ Main Review Content */}
      <div className="review-container">
        <h2>ðŸ“š Review Flashcards</h2>

        <div className="filter-buttons">
          <button onClick={() => handleFilter(1)}>1 Mark ({countByMarks(1)})</button>
          <button onClick={() => handleFilter(2)}>2 Marks ({countByMarks(2)})</button>
          <button onClick={() => handleFilter(5)}>5 Marks ({countByMarks(5)})</button>
          <button onClick={() => handleFilter(10)}>10 Marks ({countByMarks(10)})</button>
        </div>

        {!selectedMark && <p>Please select a mark type to review questions.</p>}

        {selectedMark && filteredCards.length === 0 && (
          <p>No flashcards found for {selectedMark} Marks.</p>
        )}

        {selectedMark && filteredCards.length > 0 && (
          <div className="card-list">
            {filteredCards.map((card, index) => (
              <div key={index} className="flashcard">
                <div className="question">Q: {card.question}</div>
                <div className="answer">A: {card.answer}</div>
                <div className="marks">ðŸŽ¯ Marks: {card.marks}</div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ReviewPage;
